<body>
    <div class="container-sm wrapper">
        <form action="api.php" method="post">
            <h4>Silahkan Input Data Belanja Anda</h4>
            <hr>
            <div class="form-group">
                <label for="id_barang">ID Barang</label>
                <input type="text" name="id_barang" class="form-control form-control-lg" id="id_barang" aria-describedby="id_barang">
            </div>
            <div class="form-group">
                <label for="nama_barang">Nama Barang</label>
                <input type="text" name="nama_barang" class="form-control form-control-lg" id="nama_barang" aria-describedby="nama_barang">
            </div>
            <div class="input-group mb-3">
                <div class="input-group-prepend">
                    <span class="input-group-text" id="basic-addon1" style="width:10em;">Satuan Barang</span>
                </div>
                <input type="text" class="form-control form-control-lg" name="satuan" placeholder="" aria-label="Username" aria-describedby="basic-addon1">
            </div>
            <div class="input-group mb-3">
                <div class="input-group-prepend">
                    <span class="input-group-text" style="width:10em;">Rp.</span>
                </div>
                <input type="text" class="form-control form-control-lg" name="harga" aria-label="Amount (to the nearest rupiah)">
                <div class="input-group-append">
                    <span class="input-group-text">.00</span>
                </div>
            </div>
            <button type="submit" name="submit" class="btn btn-success btn-lg">Submit</button>
        </form>
    </div>




